VAR_INPUT
    // ç”µæœºæŽ§åˆ¶è¾“å…¥
    Start : BOOL;                    // å¯åŠ¨ä¿¡å·
    Stop : BOOL;                     // åœæ­¢ä¿¡å·
    EmergencyStop : BOOL;            // æ€¥åœä¿¡å·

    // PIDæŽ§åˆ¶è¾“å…¥
    Setpoint : REAL;                 // è®¾å®šå€¼
    Feedback : REAL;                 // åé¦ˆå€¼
    EnablePID : BOOL;                // PIDä½¿èƒ½

    // PIDå‚æ•°
    Kp : REAL := 1.0;                // æ¯”ä¾‹å¢žç›Š
    Ki : REAL := 0.1;                // ç§¯åˆ†å¢žç›Š
    Kd : REAL := 0.05;               // å¾®åˆ†å¢žç›Š
    MaxOutput : REAL := 100.0;       // æœ€å¤§è¾“å‡ºé™åˆ¶
    MinOutput : REAL := -100.0;      // æœ€å°è¾“å‡ºé™åˆ¶
    MaxIntegral : REAL := 100.0;     // ç§¯åˆ†æŠ—é¥±å’Œé™åˆ¶
END_VAR

VAR_OUTPUT
    MotorRunning : BOOL;             // ç”µæœºè¿è¡ŒçŠ¶æ€
    PIDOutput : REAL;                // PIDè¾“å‡ºå€¼
    Command : REAL;                  // æœ€ç»ˆæŽ§åˆ¶å‘½ä»¤
    Error : REAL;                    // å½“å‰è¯¯å·®
END_VAR

VAR
    // PIDæŽ§åˆ¶å˜é‡
    Integral : REAL := 0.0;          // ç§¯åˆ†é¡¹
    PrevError : REAL := 0.0;         // ä¸Šæ¬¡è¯¯å·®
    Derivative : REAL := 0.0;        // å¾®åˆ†é¡¹

    // çŠ¶æ€å˜é‡
    bMotorEnabled : BOOL := FALSE;   // ç”µæœºä½¿èƒ½çŠ¶æ€
END_VAR

// ç”µæœºå¯åœæŽ§åˆ¶é€»è¾‘
IF Start AND NOT EmergencyStop THEN
    bMotorEnabled := TRUE;
END_IF;

IF Stop OR EmergencyStop THEN
    bMotorEnabled := FALSE;
    // åœæ­¢æ—¶å¤ä½PIDå˜é‡
    Integral := 0.0;
    PrevError := 0.0;
END_IF;

MotorRunning := bMotorEnabled;

// PIDæŽ§åˆ¶é€»è¾‘
IF bMotorEnabled AND EnablePID THEN
    // è®¡ç®—è¯¯å·®
    Error := Setpoint - Feedback;

    // ç§¯åˆ†é¡¹è®¡ç®—ï¼ˆå¸¦æŠ—é¥±å’Œï¼‰
    Integral := Integral + Error;
    IF Integral > MaxIntegral THEN
        Integral := MaxIntegral;
    ELSIF Integral < -MaxIntegral THEN
        Integral := -MaxIntegral;
    END_IF;

    // å¾®åˆ†é¡¹è®¡ç®—
    Derivative := Error - PrevError;

    // PIDè¾“å‡ºè®¡ç®—
    PIDOutput := Kp * Error + Ki * Integral + Kd * Derivative;

    // è¾“å‡ºé’³ä½
    IF PIDOutput > MaxOutput THEN
        Command := MaxOutput;
    ELSIF PIDOutput < MinOutput THEN
        Command := MinOutput;
    ELSE
        Command := PIDOutput;
    END_IF;

    // æ›´æ–°ä¸Šæ¬¡è¯¯å·®
    PrevError := Error;
ELSE
    // PIDæœªä½¿èƒ½æ—¶è¾“å‡ºä¸º0
    Command := 0.0;
    PIDOutput := 0.0;
    Error := 0.0;
END_IF;

END_FUNCTION_BLOCK
